jQuery(document).ready(function(){jQuery(".kspoiler").click(function(){if(!jQuery(".kspoiler-content").is(":visible")){jQuery(this).find(".kspoiler-content").show();jQuery(this).find(".kspoiler-expand").hide();jQuery(this).find(".kspoiler-hide").show();}else{jQuery(this).find(".kspoiler-content").hide();jQuery(this).find(".kspoiler-expand").show();jQuery(this).find(".kspoiler-hide").hide();}});jQuery(".openmodal").click(function(){var a=jQuery(this).attr("href");jQuery(a).css("visibility","visible");});jQuery(".heading").click(function(){if(!jQuery(this).hasClass("heading-less")){jQuery(this).prev(".heading").show();jQuery(this).hide();jQuery(this).next(".content").slideToggle(500);}else{var a=jQuery(this).next(".heading").show();jQuery(this).hide();a.next(".content").slideToggle(500);}});jQuery("#kmod_topics").change(function(){var a=jQuery(this).val();if(a!=0){jQuery("#kmod_subject").hide();}else{jQuery("#kmod_subject").show();}if(a==-1){jQuery("#kmod_targetid").show();}else{jQuery("#kmod_targetid").hide();}});jQuery("#kmod_categories").change(function(){jQuery.getJSON(kunena_url_ajax,{catid:jQuery(this).val()}).done(function(b){var c=jQuery("#kmod_topics option:nth(0)").clone();var a=jQuery("#kmod_topics option:nth(1)").clone();jQuery("#kmod_topics").empty();c.appendTo("#kmod_topics");a.appendTo("#kmod_topics");jQuery.each(b,function(e,d){jQuery.each(d,function(g,f){jQuery("#kmod_topics").append('<option value="'+f.id+'">'+f.subject+"</option>");});});});});});